mapper.update:
UPDATE choice_question
SET paper_id =#{id}
WHERE
    paper_id IS NULL
    AND teacher_name = #{teacherName}

mapper.select:
SELECT id, question, items, answer, type, degree, points, user_name, question_score FROM question WHERE id in
#{item}
order by id desc

SELECT id, question, items, answer, type, degree, points, user_name, question_score FROM question WHERE id in
#{item}
order by type asc
